import React, { Component } from 'react'

import File from './File.js'

class Files extends Component {
  constructor(){
    super();

    this.state = {
      fileName: ""
    }
  }

  addFile = (fileId) => {
    var file = this.props.files.map.filer(function(file){
      return file.id === fileId
    });

    this.setState({
      fileName: file.name
    })
  }

  render() {
    return (
      <React.Fragment>
        <h3>Recent</h3>
         <ul>
          {
              this.props.files.map(function(file){
              return (
              <div>
                <File nameFile = {file.name}
                      date = {file.added_at} />
                    <button onClick={() => this.addFile(file.id) }>
                    <i className="fa fa-download"></i>
                    </button>
              </div>
                  )
            })
          }
         </ul>
         <div>
           {this.state.fileName}
         </div>
      </React.Fragment>
    )
  }
}

export default Files
